# <bitbar.image>https://i.imgur.com/Tohefs7.png</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>

import requests

# =================================

# EDIT THESE SETTINGS

# You can find your key here https://www.wanikani.com/settings/account

key = ""

# =================================

# CODE BEGINS HERE

# =================================

url_study = f"https://www.wanikani.com/api/user/{key}/study-queue"
url_srs = f"https://www.wanikani.com/api/user/{key}/srs-distribution"

image = ""

response = requests.request("GET", url_study)
data = response.json()

lessons = data['requested_information']['lessons_available']
reviews = data['requested_information']['reviews_available']

if lessons > 0:
    print(f'{lessons} Lessons | templateImage={image}')
elif reviews > 0:
    print(f'{reviews} Reviews | templateImage={image}')
else:
    print(f' | templateImage={image}')

print('---')

username = data['user_information']['username']
title = data['user_information']['title']

print(f'{username} | href=https://community.wanikani.com/u/{username}')
print(f'Sect. {title}')

print('---')

print('LESSONS & REVIEWS | size=10')

print(f'Lessons - {lessons} | href=https://www.wanikani.com/lesson')
print(f'Reviews - {reviews} | href=https://www.wanikani.com/review')

print('---')

print('STATISTICS | size=10')

response = requests.request("GET", url_srs)
data = response.json()

levels = ['apprentice', 'guru', 'master', 'enlighten', 'burned']

def get_stat(level):
    return str(data['requested_information'][level]['total'])

for level in levels:
    total = get_stat(level)
    level = level.capitalize()
    print(f'{level} - {total}')